mapper.select:
SELECT T1.USER_ID AS USER_ID ,T1.LATN_ID AS LATN_ID ,T1.GRP_LATN_ID AS GRP_LATN_ID ,T1.GRP_MEM_LATN_ID AS GRP_MEM_LATN_ID ,T1.USER_NAME AS USER_NAME ,T1.USER_PASS AS USER_PASS ,T1.DEPART_ID AS DEPART_ID ,ZEROIFNULL(T1.TEAM_ID) AS TEAM_ID ,T1.USER_ROLE AS USER_ROLE ,T4.ROLE_NAME AS ROLE_NAME ,T1.USER_STATUS AS USER_STATUS ,T1.LAST_LOGIN_TIME AS LAST_LOGIN_TIME ,T1.CREATOR_NAME AS CREATOR_NAME ,T1.CREATE_TIME AS CREATE_TIME ,T1.NICK_NAME AS NICK_NAME ,T1.USER_IP AS USER_IP ,T1.USER_EMAIL AS USER_EMAIL ,T1.USER_PHONE AS USER_PHONE ,T1.QUERY_RIGHT AS QUERY_RIGHT ,T1.DATA_RIGHT AS DATA_RIGHT ,T1.LAST_MODIFY_PSW_TIME AS LAST_MODIFY_PSW_TIME ,T1.CONTINUOUS_LOGIN_FAIL_CNT AS CONTINUOUS_LOGIN_FAIL_CNT ,T2.LATN_CD AS LATN_CD ,T3.DEPART_NAME AS DEPART_NAME FROM ${database}.SYS_USER T1 LEFT JOIN ${database}.SYS_LOC_LATN T2 ON T1.LATN_ID=T2.LATN_ID LEFT JOIN ${database}.SYS_DEPART T3 ON T1.DEPART_ID = T3.DEPART_ID LEFT JOIN ${database}.SYS_ROLE T4 ON T1.USER_ROLE = T4.ROLE_ID WHERE 1=1
AND T1.USER_ID like '%'|| #{userId}||'%'
QUALIFY row_number() over(order by T1.USER_ID DESC) between ${beginRow} and ${endRow};
